# Day 1
# part 1
input = '';
doubled = []
input.chars.each_cons(2) do |pair|
  doubled << pair[0] if pair.uniq.length == 1
end;
doubled << input.first if input.first == input.last
doubled.map(&:to_i).sum

# part 2
doubled = []
input.chars.each_with_index do |char, i|
  doubled << char if input[(input.length/2 + i)%input.length] == char
end;
doubled.map(&:to_i).sum


