# part 2
input.map do |array|
  x = nil
  array.each_with_index  do |element1, i|
    array.each_with_index do |element2, j|
      next unless j > i
      x = element1/element2 if element1%element2 == 0
      x = element2/element1 if element2%element1 == 0
    end
  end
  x
end.sum

# Day 3
# part 1
x = 0
y = 0
distance = 1
travelled = 0
times_travelled = 0
array = []
dir = :e
1.upto(400_000) do |n|
  array[n] = {x: x, y: y}
  travelled += 1
  case dir
  when :e
    x += 1
    dir = :n if travelled == distance
  when :n
    y += 1
    dir = :w if travelled == distance
  when :w
    x -= 1
    dir = :s if travelled == distance
  when :s
    y -= 1
    dir = :e if travelled == distance
  end
  if travelled == distance
    times_travelled += 1
    travelled = 0
    if times_travelled == 2
      times_travelled = 0
      distance += 1
    end
  end
end
array[312051]

# part 2
x = 0
y = 0
distance = 1
travelled = 0
times_travelled = 0
array = []
dir = :e
1.upto(400_000) do |n|
  total = 0
  -1.upto(1) do |i|
    -1.upto(1) do |j|
      next if i.zero? && j.zero?
      entry = array.find do |hash|
        next if hash.nil?
        hash.fetch(:x) == x+i && hash.fetch(:y) == y+j
      end
      total += entry[:value] if entry
    end
  end

  total = 1 if n == 1
  break total if total > 312051
  array[n] = {x: x, y: y, value: total}
  travelled += 1
  case dir
  when :e
    x += 1
    dir = :n if travelled == distance
  when :n
    y += 1
    dir = :w if travelled == distance
  when :w
    x -= 1
    dir = :s if travelled == distance
  when :s
    y -= 1
    dir = :e if travelled == distance
  end
  if travelled == distance
    times_travelled += 1
    travelled = 0
    if times_travelled == 2
      times_travelled = 0
      distance += 1
    end
  end
end

