def get_subweight(input, bottom)
  subs = input[bottom][:subs]
  if subs.empty?
    input[bottom][:weight]
  else
    weights = subs.map { |sub| get_subweight(input, sub) }
    raise "#{bottom} #{weights.inspect}" if weights.uniq.length != 1
    input[bottom][:weight] + weights.sum
  end
end
