score = 0
level = 0
skip_next = false
ignore = false
input.each do |char|
  if skip_next
    skip_next = false
    next
  end
  case char
  when '{'
    unless ignore
      level += 1
      score += level
    end
  when '}'
    level -= 1 unless ignore || level == 0
  when '<'
    ignore = true
  when '>'
    ignore = false
  when '!'
    skip_next = true
  else
  end
end
