  last_pair_num = nil
  has_pair = false
  ascending = true
  pass.to_s.chars.each_cons(2) do |(c1, c2)|
    if c1 == c2
      if !has_pair
        has_pair = true unless last_pair_num == c2
        last_pair_num = c1
      elsif last_pair_num == c2
        has_pair = false
      end
