def print_screen
  puts '----'
  min_x = @coords.keys.map { |coord| coord.first }.min || -1
  max_x = @coords.keys.map { |coord| coord.first }.max || 1
  min_y = @coords.keys.map { |coord| coord.last }.min || -1
  max_y = @coords.keys.map { |coord| coord.last }.max || 1

  min_y.upto(max_y) do |y|
    min_x.upto(max_x) do |x|
      if [x,y] == [@x,@y]
        print 'o'
        next
      end
      if [x,y] == [0,0]
        print 'x'
        next
      end
      print case @coords[[x,y]]
            when 0 then '█'
            when 2 then 'X'
            else '.'
            end
    end
    puts
  end
end

@get_input = lambda {
  print_screen
  STDIN.getch
  STDIN.getch
  case STDIN.getch
  when 'A'
    @y -= 1
    1
  when 'B'
    @y += 1
    2
  when 'D'
    @x -= 1
    3
  when 'C'
    @x += 1
    4
  end
}

@coords = {}

comp = Intcode.new(program.dup);
@x = 0
@y = 0
@last = [0,0]

distance = 0
visited = {}

loop do
  response = comp.run(&@get_input)
  output = response[:output]
  break if response[:status] == :halted
  @coords[[@x, @y]] = output
  case output
  when 0
    @x, @y = @last
  when 1
    if visited[[@x,@y]]
      distance -= 1
    else
      distance += 1
    end
    @last = [@x, @y]
  when 2
    @last = [@x, @y]
  else
    @last = [@x, @y]
  end
  visited[[@x,@y]] = true
end

